function [shap]=ZV00TO(w,alpha,xd,z)%shap=ZV00TO(w,alpha,xd)%trans 1 - 0%trans 2 - 0%trans 3 - TO% w = frequency in radians% alpha = force-to-mass ratio (Umax/M)% xd = desired slew distance% z - dampingtau=2*pi/w;         %system period%Calculate impulse times% Calculate TO decel[dsh,sh]=neg221(1/tau,z,0.01);a=sh(2,1);b=sh(3,1);t1=0;t2=sqrt(3*a^2-2*a*b+xd/alpha);t3=2*t2+2*a-b;t4=t3+a;t5=t3+bt=[t1 t2 t3 t4 t5];amps=[1 -2  2 -2 1];shap=[t' amps'];